import React from 'react';

function CategoryList(props){

    const categoryClicked = category => evt => {
        props.categoryClicked(category);
    };
    return (
        <div className="layout">
           {
               props.categories.map( category =>{
                   return (
                       <div className="media items border" key = {category.id} onClick={categoryClicked(category)}>
                           <img className="mr-3" alt = "seafood" src={category.image} height ="150" weight = "150"/>
                            <div className ="media-body">
                            <h4 className="mt-0">{category.title}</h4>
                            <span>{category.description}</span>
                            <h5> ${category.price}</h5>
                            </div>
                        </div>

                   )
                })
           }

      </div>
    )
}

export default CategoryList
