    state = {
        ptoducts: [],
      }
    componentDidMount(){
        // fetch data
        fetch('http://127.0.0.1:8000/api/product/', 
        {
          method: 'GET',
          header:{

          }
        }).then( resp => resp.json())
        .then( res => this.setState({products: res}))
        .catch( error => console.log(error))
      }
    render(){
        return (
            <React.Fragment>
                {   
                    this.props.product ? (
                        <h3>{this.props.product.title}</h3>
                    ) :<h3>products={this.state.products.id}</h3>
                }

            </React.Fragment>
        )
    }

}

