import React from 'react';

function CategoryList(props){

    const categoryClicked = category => evt => {
        props.categoryClicked(category);
    };
    return (
        <div className="layout">

           {
               props.categories.map( category =>{
                   return (
                       <div className="items border" key = {category.id} onClick={categoryClicked(category)}>
                           <h3 >{category.title}</h3>
                           <img alt = "seafood" src={category.image} height ="100" weight = "100"/>

                            <h3> ${category.price}</h3>

                        </div>

                   )
                })
           }

      </div>
    )
}

export default CategoryList
